import React, { useState } from "react";
import axios from "axios";
import InputMask from "react-input-mask";

const App: React.FC = () => {
  const [cpf, setCpf] = useState("");
  const [status, setStatus] = useState<string | null>(null);
  const [error, setError] = useState<string | null>(null);
  const [loading, setLoading] = useState(false);

  // Remove caracteres nÃ£o numÃ©ricos para validaÃ§Ã£o
  const formatCpf = (cpf: string) => cpf.replace(/\D/g, "");

  const checkCpf = async () => {
    const formattedCpf = formatCpf(cpf); // Remove pontos e traÃ§os

    if (formattedCpf.length !== 11) {
      setError("O CPF deve conter exatamente 11 dÃ­gitos.");
      setStatus(null);
      return;
    }

    setLoading(true);
    setStatus(null);
    setError(null);

    try {
      const response = await axios.get(`http://127.0.0.1:5000/${formattedCpf}`);
      setStatus(response.data.status);
    } catch (error) {
      setError("Erro na consulta. Verifique se a API estÃ¡ rodando.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ textAlign: "center", marginTop: "50px" }}>
      <h2>Verificador de CPF na Blacklist</h2>

      <InputMask
        mask="999.999.999-99"
        placeholder="Digite o CPF"
        value={cpf}
        onChange={(e) => setCpf(e.target.value)}
        style={{
          padding: "10px",
          marginRight: "10px",
          border: error ? "2px solid red" : "2px solid #ccc",
          borderRadius: "5px",
        }}
      />

      <button
        onClick={checkCpf}
        style={{ padding: "10px", cursor: "pointer" }}
        disabled={loading}
      >
        {loading ? "Consultando..." : "Consultar"}
      </button>

      {error && <p style={{ color: "red" }}>{error}</p>}
      {status && (
        <p>
          Status: <strong>{status === "BLOCK" ? "ðŸš« BLOQUEADO" : "âœ… LIVRE"}</strong>
        </p>
      )}
    </div>
  );
};

export default App;
