import React, { useState } from "react";
import axios from "axios";

const App: React.FC = () => {
  const [cpf, setCpf] = useState("");
  const [status, setStatus] = useState<string | null>(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const checkCpf = async () => {
    if (!cpf.trim()) {
      alert("Por favor, digite um CPF!");
      return;
    }

    setLoading(true);
    setStatus(null);
    setError(null);

    try {
      const response = await axios.get(`http://127.0.0.1:5000/${cpf}`);
      setStatus(response.data.status);
    } catch (error) {
      setError("Erro na consulta. Verifique se a API está rodando.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ textAlign: "center", marginTop: "50px" }}>
      <h2>Verificador de CPF na Blacklist</h2>
      <input
        type="text"
        placeholder="Digite o CPF"
        value={cpf}
        onChange={(e) => setCpf(e.target.value)}
        style={{ padding: "10px", marginRight: "10px" }}
      />
      <button onClick={checkCpf} style={{ padding: "10px" }}>
        Consultar
      </button>

      {loading && <p>Consultando...</p>}
      {error && <p style={{ color: "red" }}>{error}</p>}
      {status && <p>Status: <strong>{status}</strong></p>}
    </div>
  );
};

export default App;
