import React, { useState } from "react";
import axios from "axios";

const App: React.FC = () => {
  const [cpf, setCpf] = useState("");
  const [status, setStatus] = useState<string | null>(null);
  const [error, setError] = useState<string | null>(null);
  const [loading, setLoading] = useState(false);


  const handleCpfChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    let value = e.target.value.replace(/\D/g, ""); 

    if (value.length > 11) {
      value = value.slice(0, 11); 
    }

    // mÃ¡scara XXX.XXX.XXX-XX para o cpf
    let formattedCpf = value;
    if (value.length > 3) {
      formattedCpf = value.slice(0, 3) + "." + value.slice(3);
    }
    if (value.length > 6) {
      formattedCpf = formattedCpf.slice(0, 7) + "." + formattedCpf.slice(7);
    }
    if (value.length > 9) {
      formattedCpf = formattedCpf.slice(0, 11) + "-" + formattedCpf.slice(11);
    }

    setCpf(formattedCpf);
  };

  const formatCpfForApi = (cpf: string) => cpf.replace(/\D/g, "");

  const checkCpf = async () => {
    const formattedCpf = formatCpfForApi(cpf); 

    if (formattedCpf.length !== 11) {
      setError("O CPF deve conter exatamente 11 dÃ­gitos.");
      setStatus(null);
      return;
    }

    setLoading(true);
    setStatus(null);
    setError(null);

    try {
      const response = await axios.get(`http://127.0.0.1:5000/${formattedCpf}`);
      setStatus(response.data.status);
    } catch (error) {
      setError("Erro na consulta. Verifique se a API estÃ¡ rodando.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ textAlign: "center", marginTop: "50px" }}>
      <h2>Verificador de CPF na Blacklist</h2>

      <input
        type="text"
        placeholder="Digite o CPF"
        value={cpf}
        onChange={handleCpfChange}
        maxLength={14} // Garante que o input nÃ£o ultrapasse XXX.XXX.XXX-XX
        style={{
          padding: "10px",
          marginRight: "10px",
          border: error ? "2px solid red" : "2px solid #ccc",
          borderRadius: "5px",
        }}
      />

      <button
        onClick={checkCpf}
        style={{ padding: "10px", cursor: "pointer" }}
        disabled={loading}
      >
        {loading ? "Consultando..." : "Consultar"}
      </button>

      {error && <p style={{ color: "red" }}>{error}</p>}
      {status && (
        <p>
          Status: <strong>{status === "BLOCK" ? "ðŸš« BLOQUEADO" : "âœ… FREE"}</strong>
        </p>
      )}
    </div>
  );
};

export default App;
